import styled from 'styled-components';

export const ButtonContainer = styled.button`
    padding: 20px;
    border: 1px solid #EBF5EE;
    background-color: #283044;
    color: white;
    font-size: 24px;
    font-weight: 700;
    flex: 1;
    border-radius: 3px;

    

    &:hover {
        opacity: 0.6;
    }
`
